import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

const MapView = ({ shipment }) => {
  if (!shipment) return null;

  // Default to some coordinates (Replace with actual shipment coordinates)
  const position = shipment?.coordinates || [37.7749, -122.4194]; // Default: San Francisco

  return (
    <MapContainer center={position} zoom={10} style={{ height: "400px", width: "100%" }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <Marker position={position}>
        <Popup>
          <strong>Shipment ID:</strong> {shipment.shipmentId} <br />
          <strong>Current Location:</strong> {shipment.currentLocation}
        </Popup>
      </Marker>
    </MapContainer>
  );
};

export default MapView;
