import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

const MapView = ({ shipment }) => {
  const [position, setPosition] = useState([37.7749, -122.4194]); // Default: SF

  useEffect(() => {
    if (shipment?.currentLocation) {
      fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${shipment.currentLocation}`)
        .then((res) => res.json())
        .then((data) => {
          if (data.length > 0) {
            setPosition([parseFloat(data[0].lat), parseFloat(data[0].lon)]);
          }
        })
        .catch((error) => console.error("Error fetching location:", error));
    }
  }, [shipment?.currentLocation]);

  return (
    <MapContainer center={position} zoom={10} style={{ height: "400px", width: "100%" }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <Marker position={position}>
        <Popup>
          <strong>Shipment ID:</strong> {shipment.shipmentId} <br />
          <strong>Current Location:</strong> {shipment.currentLocation}
        </Popup>
      </Marker>
    </MapContainer>
  );
};

export default MapView;
