import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

export const fetchShipments = createAsyncThunk("shipment/fetchShipments", async () => {
  const response = await axios.get("http://localhost:5000/api/shipments");
  return response.data;
});

const shipmentSlice = createSlice({
  name: "shipment",
  initialState: { shipments: [], loading: false },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchShipments.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchShipments.fulfilled, (state, action) => {
        state.loading = false;
        state.shipments = action.payload;
      })
      .addCase(fetchShipments.rejected, (state) => {
        state.loading = false;
      });
  },
});

export default shipmentSlice.reducer;
