import React, { useState } from "react";

const UpdateLocation = ({ shipmentId }) => {
  const [newLocation, setNewLocation] = useState("");

  const updateLocation = async () => {
    await fetch(`http://localhost:5000/api/shipment/${shipmentId}/update-location`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ currentLocation: newLocation }),
    });
    alert("Location updated!");
  };

  return (
    <div>
      <input
        type="text"
        className="form-control"
        placeholder="Enter new location"
        value={newLocation}
        onChange={(e) => setNewLocation(e.target.value)}
      />
      <button className="btn btn-success mt-2" onClick={updateLocation}>Update Location</button>
    </div>
  );
};

export default UpdateLocation;
