import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

// Fetch shipments from backend
export const fetchShipments = createAsyncThunk("shipments/fetchShipments", async () => {
  const response = await fetch("http://localhost:5000/api/shipments"); // Adjust URL if needed
  return response.json();
});

const shipmentSlice = createSlice({
  name: "shipments",
  initialState: { list: [], loading: false },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchShipments.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchShipments.fulfilled, (state, action) => {
        state.loading = false;
        state.list = action.payload;
      })
      .addCase(fetchShipments.rejected, (state) => {
        state.loading = false;
      });
  },
});

export default shipmentSlice.reducer;
