import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchShipments,
  fetchShipmentById,
  updateShipmentLocation,
  fetchETA,
} from "../redux/shipmentSlice";

const Dashboard = () => {
  const dispatch = useDispatch();
  const { shipments, shipmentDetails, loading } = useSelector((state) => state.shipment);
  const [selectedShipment, setSelectedShipment] = useState(null);
  const [newLocation, setNewLocation] = useState("");

  useEffect(() => {
    dispatch(fetchShipments());
  }, [dispatch]);

  const handleGetDetails = (id) => {
    dispatch(fetchShipmentById(id));
    setSelectedShipment(id);
  };

  const handleUpdateLocation = (id) => {
    if (!newLocation) return alert("Enter a new location!");
    dispatch(updateShipmentLocation({ id, location: newLocation }));
    setNewLocation("");
  };

  const handleFetchETA = (id) => {
    dispatch(fetchETA(id));
  };

  return (
    <div className="container mt-4">
      <h2>Shipments Dashboard</h2>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>ID</th>
              <th>Container ID</th>
              <th>Current Location</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {shipments.map((shipment) => (
              <tr key={shipment._id}>
                <td>{shipment._id}</td>
                <td>{shipment.containerId}</td>
                <td>{shipment.currentLocation}</td>
                <td>
                  <button
                    className="btn btn-primary me-2"
                    onClick={() => handleGetDetails(shipment._id)}
                  >
                    Get Full Details
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {selectedShipment && shipmentDetails && (
        <div className="mt-4">
          <h3>Shipment Details</h3>
          <p><strong>ID:</strong> {shipmentDetails._id}</p>
          <p><strong>Container ID:</strong> {shipmentDetails.containerId}</p>
          <p><strong>Current Location:</strong> {shipmentDetails.currentLocation}</p>
          <p><strong>ETA:</strong> {shipmentDetails.eta || "Not available"}</p>
          
          <button
            className="btn btn-info me-2"
            onClick={() => handleFetchETA(selectedShipment)}
          >
            Get ETA
          </button>

          <input
            type="text"
            className="form-control d-inline w-auto mx-2"
            placeholder="New Location"
            value={newLocation}
            onChange={(e) => setNewLocation(e.target.value)}
          />

          <button
            className="btn btn-success"
            onClick={() => handleUpdateLocation(selectedShipment)}
          >
            Update Location
          </button>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
