import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { fetchShipmentById } from "../redux/shipmentSlice";
import MapView from "./MapView";
import UpdateLocation from "./UpdateLocation";

const ShipmentDetails = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const shipment = useSelector((state) => state.shipments.currentShipment);
  const loading = useSelector((state) => state.shipments.loading);
  const error = useSelector((state) => state.shipments.error);

  useEffect(() => {
    dispatch(fetchShipmentById(id)); // Fetch shipment details dynamically
  }, [dispatch, id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!shipment) return <p>Shipment not found.</p>;

  return (
    <div className="container mt-4">
      <h2>Shipment Details</h2>
      <p><strong>Shipment ID:</strong> {shipment.shipmentId}</p>
      <p><strong>Container ID:</strong> {shipment.containerId}</p>
      <p><strong>Route:</strong> {shipment.route}</p>
      <p><strong>Current Location:</strong> {shipment.currentLocation}</p>
      <p><strong>Estimated Arrival:</strong> {shipment.estimatedArrival}</p>

      <div className="mt-3">
        <MapView shipment={shipment} />
      </div>

      <div className="mt-3">
        <UpdateLocation shipmentId={id} />
      </div>
    </div>
  );
};

export default ShipmentDetails;
