import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// Async action to fetch shipments
export const fetchShipments = createAsyncThunk(
  "shipments/fetchShipments",
  async () => {
    const response = await axios.get("http://localhost:5000/api/shipments");
    return response.data;
  }
);

// Async action to update shipment location
export const updateShipmentLocation = createAsyncThunk(
  "shipments/updateLocation",
  async ({ shipmentId, newLocation }) => {
    const response = await axios.post(
      `http://localhost:5000/api/shipment/${shipmentId}/update-location`,
      { location: newLocation }
    );
    return response.data;
  }
);

// Async action to fetch ETA for a shipment
export const fetchETA = createAsyncThunk(
  "shipments/fetchETA",
  async (shipmentId) => {
    const response = await axios.get(
      `http://localhost:5000/api/shipment/${shipmentId}/eta`
    );
    return response.data;
  }
);

const shipmentSlice = createSlice({
  name: "shipment",
  initialState: {
    shipments: [],
    loading: false,
    error: null,
    eta: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchShipments.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchShipments.fulfilled, (state, action) => {
        state.loading = false;
        state.shipments = action.payload;
      })
      .addCase(fetchShipments.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      })
      .addCase(updateShipmentLocation.fulfilled, (state, action) => {
        // Update shipment location in state
        const index = state.shipments.findIndex(
          (shipment) => shipment._id === action.payload._id
        );
        if (index !== -1) {
          state.shipments[index].currentLocation = action.payload.currentLocation;
        }
      })
      .addCase(fetchETA.fulfilled, (state, action) => {
        state.eta = action.payload.eta;
      });
  },
});

export default shipmentSlice.reducer;
