import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchShipments, updateShipmentLocation, fetchETA } from "../redux/shipmentSlice";
import FilterSort from "./FilterSort";
import MapView from "./MapView";
import axios from "axios";

const Dashboard = () => {
  const dispatch = useDispatch();
  const { shipments, loading } = useSelector((state) => state.shipment);
  const [filteredShipments, setFilteredShipments] = useState([]);
  const [selectedShipment, setSelectedShipment] = useState(null);
  const [eta, setEta] = useState(null);
  const [newLocation, setNewLocation] = useState("");

  useEffect(() => {
    dispatch(fetchShipments());
  }, [dispatch]);

  useEffect(() => {
    setFilteredShipments(shipments);
  }, [shipments]);

  const handleFilter = (status) => {
    setFilteredShipments(
      status ? shipments.filter((s) => s.status === status) : shipments
    );
  };

  const handleSort = (criteria) => {
    const sorted = [...filteredShipments].sort((a, b) =>
      criteria === "eta"
        ? new Date(a.estimatedArrival) - new Date(b.estimatedArrival)
        : a.currentLocation.localeCompare(b.currentLocation)
    );
    setFilteredShipments(sorted);
  };

  const handleUpdateLocation = async (shipmentId) => {
    if (!newLocation) return alert("Enter a new location!");
    try {
      await axios.post(`/shipment/${shipmentId}/update-location`, { location: newLocation });
      dispatch(fetchShipments()); // Refresh shipments after updating location
      setNewLocation("");
      alert("Shipment location updated successfully!");
    } catch (error) {
      console.error("Error updating location:", error);
    }
  };

  const handleFetchETA = async (shipmentId) => {
    try {
      const response = await axios.get(`/shipment/${shipmentId}/eta`);
      setEta(response.data.eta);
    } catch (error) {
      console.error("Error fetching ETA:", error);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Shipments Dashboard</h2>
      <FilterSort onFilter={handleFilter} onSort={handleSort} />

      {loading ? (
        <p>Loading...</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>ID</th>
              <th>Container ID</th>
              <th>Current Location</th>
              <th>ETA</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredShipments.map((shipment) => (
              <tr key={shipment._id}>
                <td>{shipment.shipmentId}</td>
                <td>{shipment.containerId}</td>
                <td>{shipment.currentLocation}</td>
                <td>{new Date(shipment.estimatedArrival).toLocaleDateString()}</td>
                <td>{shipment.status}</td>
                <td>
                  <button
                    className="btn btn-primary me-2"
                    onClick={() => setSelectedShipment(shipment)}
                  >
                    Map View
                  </button>
                  <button
                    className="btn btn-info me-2"
                    onClick={() => handleFetchETA(shipment._id)}
                  >
                    Get ETA
                  </button>
                  <input
                    type="text"
                    className="form-control d-inline w-auto mx-2"
                    placeholder="New Location"
                    value={newLocation}
                    onChange={(e) => setNewLocation(e.target.value)}
                  />
                  <button
                    className="btn btn-success"
                    onClick={() => handleUpdateLocation(shipment._id)}
                  >
                    Update Location
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {eta && <p className="mt-3">Estimated Time of Arrival: {eta}</p>}

      {selectedShipment && (
        <div className="mt-4">
          <h3>Map View for Shipment ID: {selectedShipment.shipmentId}</h3>
          <MapView location={{ lat: 51.505, lng: -0.09 }} shipment={selectedShipment} />
        </div>
      )}
    </div>
  );
};

export default Dashboard;
